import * as React from 'react';
import { Component } from 'react';
import { arrayBuffer } from 'stream/consumers';
import { Hero } from "../components/Hero";
import { heroRoster } from "../components/heroRoster";


interface Props {
    draftableHeroes: Hero[],
    draftableHeroesNames: String[],
    draftableHeroKeyTest: String,
    enemyHeroes: String[],
    allyHeroes: String[]
    onHeroPick: any //MAKE SURE YOU GIVE THIS THE PROPER TYPE!
  }

class HeroList extends React.Component<Props> {





    render() { 

        //let heroList: Hero[] = heroRoster._heroes THIS IS WHAT IM GOING FOR
        const { allyHeroes } = this.props
        let allyStringTest: string[] = ['berserker', 'warrior', 'archer']
        let enemyStringTest: string[] = ['badHero', 'youchas', 'darkKnight']
        let pick = this.props.draftableHeroes
        console.log(this.props.draftableHeroes)
        let testStringArray: String[] = ['one', 'two', 'three']
        let testObjectArray: Object[] = [{one: 1}, {two: 2}, {three: 3}, {four: 4}]


        let draftableHeroes = [...this.props.draftableHeroes]
        draftableHeroes = heroRoster._heroes
        let dHlength = draftableHeroes.length
        let draftableHeroesNames: String[] = []
        for (let i = 0; i < dHlength; i++) {
            draftableHeroesNames.push(draftableHeroes[i]._name)
        }
        this.setState({ draftableHeroesNames });


        allyHeroes[0] = 'haha';
        const enemyHeroes = [...this.props.enemyHeroes];
        enemyHeroes[0] = 'darkKnight';
        this.setState({ allyHeroes });
        this.setState({ enemyHeroes });




        return ( 
            <div>
                <h1>Draft Pool</h1>
                <div>
                    {heroRoster._heroes.map(hero => (
                        <button 
                            onClick={() => this.props.onHeroPick(hero)} //this actually has to be a function that will end up bringing the HERO into the onHeroPick handler
                            className="btn m-2 btn-primary">{hero._name}</button> 
                    ))}
                </div>
                <div>
                    {this.props.draftableHeroes.map(hero => (
                        <button className="btn m-2 btn-primary">{hero._name}</button> 
                    ))}
                </div>
                <h1>Your Team</h1>
                <div>
                    {allyStringTest.map(hero => (
                        <button className="btn m-2 btn-primary">{hero}</button> 
                    ))}
                </div>

                <h1>Enemy Team</h1>
                <div>
                    {enemyStringTest.map(hero => (
                        <button className="btn m-2 btn-primary">{hero}</button> 
                    ))}
                </div>
                <div>
                    {this.props.enemyHeroes.map(hero => (
                        <button className="btn m-2 btn-primary">{hero}</button> 
                    ))}
                </div>
            </div>
        );
    }
}

export default HeroList;
