import * as React from 'react';
import { Component } from 'react';
import { arrayBuffer } from 'stream/consumers';
import { CardSchema } from '../data/cardSchema';
import Grid from './grid'
import { PickPhase } from '../components/pickPhase'
import { AnyARecord } from 'dns';


interface Props {
    draftableHeroes: CardSchema[],
    draftableHeroesNames: String[],
    draftableHeroKeyTest: String,
    enemyHeroes: String[],
    allyHeroes: String[],
    onHeroPick: any, //MAKE SURE YOU GIVE THIS THE PROPER TYPE!
    pickPhase: PickPhase
  }

interface State {
    allyBoard: [
        {position: number, hero: String | null},
        {position: number, hero: String | null},
        {position: number, hero: String | null},
        {position: number, hero: String | null},
        {position: number, hero: String | null},
        {position: number, hero: String | null},
        {position: number, hero: String | null},
        {position: number, hero: String | null},
        {position: number, hero: String | null},
    ]
    enemyBoard: [        
        {position: number, hero: String | null},
        {position: number, hero: String | null},
        {position: number, hero: String | null},
        {position: number, hero: String | null},
        {position: number, hero: String | null},
        {position: number, hero: String | null},
        {position: number, hero: String | null},
        {position: number, hero: String | null},
        {position: number, hero: String | null},
    ]
}



class HeroList extends React.Component<Props, State> {
    constructor(props: Props) {
        super(props);

    this.state = {
        allyBoard: [
            {position: 0, hero: null},
            {position: 1, hero: null},
            {position: 2, hero: null},
            {position: 3, hero: null},
            {position: 4, hero: null},
            {position: 5, hero: null},
            {position: 6, hero: null},
            {position: 7, hero: null},
            {position: 8, hero: null}
        ],
        enemyBoard: [
            {position: 0, hero: null},
            {position: 1, hero: null},
            {position: 2, hero: null},
            {position: 3, hero: null},
            {position: 4, hero: null},
            {position: 5, hero: null},
            {position: 6, hero: null},
            {position: 7, hero: null},
            {position: 8, hero: null},
        ]
    }
}

    handlePlacePick = (place: any) => { //SHOULD CORRECTLY TYPE THIS
        if (this.props.pickPhase === 'placing') {
            let index = this.props.allyHeroes.length
            let hero = this.props.allyHeroes[index]

            let allyBoard = [...this.state.allyBoard]
            allyBoard[index].hero = hero
            console.log(allyBoard)
            //this.setState({ allyBoard })


        }
        }


    render() { 

        //let heroList: Hero[] = heroRoster._heroes THIS IS WHAT IM GOING FOR
        const { allyHeroes, enemyHeroes} = this.props
        let allyStringTest: string[] = ['berserker', 'warrior', 'archer']
        let enemyStringTest: string[] = ['badHero', 'youchas', 'darkKnight']
        let pick = this.props.draftableHeroes
        //console.log(this.props.draftableHeroes)
        let testStringArray: String[] = ['one', 'two', 'three']
        let testObjectArray: Object[] = [{one: 1}, {two: 2}, {three: 3}, {four: 4}]

        //let draftableHeroes = this.props.draftableHeroes
        //draftableHeroes = heroRoster._heroes
        //this.setState({ draftableHeroes });







        return ( 
            <div>
                <h1>Draft Pool</h1>
                <div>
                    {this.props.draftableHeroes.map(hero => (
                        <button 
                            onClick={() => this.props.onHeroPick(hero)} 
                            className="btn m-2 btn-primary">{hero.name}</button> 
                    ))}
                </div>
                <h1>Your Team</h1>
                <Grid
                    pickPhase={this.props.pickPhase}
                    onPlacePick={this.handlePlacePick}
                    allyBoard={this.state.allyBoard}
                />
                <div>
                    {this.props.allyHeroes.map(hero => (
                        <button className="btn m-2 btn-primary">{hero}</button> 
                    ))}
                </div>

                <h1>Enemy Team</h1>
                <Grid
                    pickPhase={this.props.pickPhase}
                    onPlacePick={this.handlePlacePick(this.state.allyBoard[0])}
                    allyBoard={this.state.allyBoard}
                />  

                <div>
                    {this.props.enemyHeroes.map(hero => (
                        <button className="btn m-2 btn-primary">{hero}</button> 
                    ))}
                </div>
            </div>
        );
    }
}

export default HeroList;
