import * as React from 'react';
import { Component } from 'react';
import { PickPhase } from '../components/pickPhase'
import { BoardPositionSchema } from '../data/boardPositionSchema';
import { GamePhase } from '../components/gamePhase'


interface Props {
  onPlacePick: any
  pickPhase: PickPhase
  board: BoardPositionSchema[]
  gamePhase: GamePhase
  onMove: any //CAN I TYPE THIS?
}

class Grid extends React.Component<Props> {


    buttonFunction = (i: number, h: String | null) => {
      if (this.props.gamePhase === 'draftPhase') {
        return this.props.onPlacePick(i)
      }
      if (this.props.gamePhase === 'movePhase') {
        return this.props.onMove(i)
      }
      return '' //I still don't know what to return for onClicks that I don't want to do anything??
    }

    render() { 

        return ( 

          /*//EXPERIMENTAL MAPPYING
          <div className="container">
            {this.props.board.map(hero => (
                        <button 
                            key={hero.position}
                            onClick={() => this.props.onPlacePick(hero.position)} 
                            className="btn m-2 btn-primary">{hero.hero}</button>))} 
          </div>*/
          //EXPERIMENTAL MAPPING


            <div className="container">
  <div className="row">
    <div className="col-sm outline">
      <button 
        onClick={() => this.buttonFunction(0, this.props.board[0].hero)} 
        className="btn m-2 btn-primary tile">{this.props.board[0].hero}
      </button>
    </div>
    <div className="col-sm outline">
      <button 
        onClick={() => this.buttonFunction(1, this.props.board[1].hero)} 
        className="btn m-2 btn-primary tile">{this.props.board[1].hero}
      </button>
    </div>
    <div className="col-sm outline">
      <button 
        onClick={() => this.buttonFunction(2, this.props.board[2].hero)} 
        className="btn m-2 btn-primary tile">{this.props.board[2].hero}
      </button>
    </div>
  </div>
  <div className="row">
    <div className="col-sm outline">
      <button 
        onClick={() => this.buttonFunction(3, this.props.board[3].hero)} 
        className="btn m-2 btn-primary tile">{this.props.board[3].hero}
      </button>
    </div>
    <div className="col-sm outline">
      <button 
        onClick={() => this.buttonFunction(4, this.props.board[0].hero)} 
        className="btn m-2 btn-primary tile">{this.props.board[4].hero}
      </button>
    </div>
    <div className="col-sm outline">
      <button 
        onClick={() => this.buttonFunction(5)} 
        className="btn m-2 btn-primary tile">{this.props.board[5].hero}
      </button>
    </div>
  </div>
  <div className="row">
    <div className="col-sm outline">
      <button 
        onClick={() => this.buttonFunction(5)} 
        className="btn m-2 btn-primary tile">{this.props.board[6].hero}
      </button>
    </div>
    <div className="col-sm outline">
      <button 
        onClick={() => this.buttonFunction(7)} 
        className="btn m-2 btn-primary tile">{this.props.board[7].hero}
      </button>
    </div>
    <div className="col-sm outline">
      <button 
        onClick={() => this.buttonFunction(8)} 
        className="btn m-2 btn-primary tile">{this.props.board[8].hero}
      </button>
    </div>
  </div>
</div>
         );
    }
}

export default Grid;
