        <h1>Brimstone Valley Draft AI</h1>
        <p>
          Pick some Heroes!
        </p>
        <HeroList
          allyHeroes={this.state.allyHeroes}
          enemyHeroes={this.state.enemyHeroes}
          draftableHeroes={this.state.draftableHeroes}
